pan&gt; pre_sum = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, suf_sum = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;function&#34;&gt;priority_queue&amp;lt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;&amp;gt; &lt;span class=&#34;title&#34;&gt;pre_max&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(nums.begin(), nums.begin()+n)&lt;/span&gt;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        priority_queue&amp;lt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;, vector&amp;lt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;&amp;gt;, greater&amp;lt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;&amp;gt;&amp;gt; &lt;span class=&#34;built_in&#34;&gt;suf_min&lt;/span&gt;(nums.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;()-n, nums.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        pre_sum = &lt;span class=&#34;built_in&#34;&gt;reduce&lt;/span&gt;(nums.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;(), nums.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;()+n, &lt;span class=&#34;number&#34;&gt;0ll&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        suf_sum = &lt;span class=&#34;built_in&#34;&gt;reduce&lt;/span&gt;(nums.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;()-n, nums.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;(), &lt;span class=&#34;number&#34;&gt;0ll&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        pre[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;] = pre_sum;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        suf[n] = suf_sum;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;; i &amp;lt; n; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            pre[i&lt;span class=&#34;number&#34;&gt;+1&lt;/span&gt;] = pre[i];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (pre_max.&lt;span class=&#34;built_in&#34;&gt;top&lt;/span&gt;() &amp;gt; nums[n+i])&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                pre[i&lt;span class=&#34;number&#34;&gt;+1&lt;/span&gt;] += nums[n+i];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                pre_max.&lt;span class=&#34;built_in&#34;&gt;push&lt;/span&gt;(nums[n+i]);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                pre[i&lt;span class=&#34;number&#34;&gt;+1&lt;/span&gt;] -= pre_max.&lt;span class=&#34;built_in&#34;&gt;top&lt;/span&gt;();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                pre_max.&lt;span class=&#34;built_in&#34;&gt;pop&lt;/span&gt;();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            suf[n&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;-i] = suf[n-i];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (suf_min.&lt;span class=&#34;built_in&#34;&gt;top&lt;/span&gt;() &amp;lt; nums[&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;*n&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;-i])&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                suf[n&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;-i] += nums[&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;*n&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;-i];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                suf_min.&lt;span class=&#34;built_in&#34;&gt;push&lt;/span&gt;(nums[&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;*n&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;-